$(function () {
    const tabelData = $('#tabelData');

    tabelData.DataTable({
        processing: true,
        serverSide: true,
        ajax: {
            url: 'server/tabel',
            type: 'POST'
        },
        autoWidth: false,
        responsive: true,
        search: { return: true },
        dom: 'Blfrtip',
        buttons: [
            {
                text: 'Add',
                action: () => { window.location.href = 'server/add' }
            },
            {
                text: 'Reload',
                action: function (e, dt, node, config) { dt.ajax.reload(function () { swal.fire('Informasi', 'Berhasil reload data tabel', 'success') }, false) }
            },
            { extend: 'excelHtml5' }
        ],
        columns: [{ data: 0 }, { data: 1 }, { data: 2 }, { data: 3 }, { data: 4 }, { data: 5 }, { data: 6 }, { data: 7 }, { data: 8 }, { data: 9 }, { data: 10 },
        { data: 11 }, { data: 12 }, { data: 13 }, { data: 14 }, { data: 15 }, { data: 16 }, { data: 17 }, { data: 18 }, { data: 19 }, { data: 20 }, { data: 21 }],
        columnDefs: [
            { targets: '_all', className: 'dt-head-center' },
            { targets: [3, 4, 5, 6, 7, 8], className: 'dt-body-center' },
            {
                targets: [9],
                orderable: false,
                className: 'dt-body-right',
                // render: $.fn.dataTable.render.number('.', ',', 2)
                // render: function (data, type, row) {
                //     return type === 'export' ? data.replace(/\D/g, '') : data;
                // }

            }
        ],
        order: [[0, 'asc']]
    });
});

